package ort.argentina.yatay.tp3.tp3_parcial_grupal3.domain.repository

import kotlinx.coroutines.flow.Flow
import ort.argentina.yatay.tp3.tp3_parcial_grupal3.data.local.dao.TaskDao
import ort.argentina.yatay.tp3.tp3_parcial_grupal3.data.local.entity.TaskEntity
import javax.inject.Inject
import javax.inject.Singleton

/**
 * Repositorio para operaciones de tareas usando Room
 */
@Singleton
class TaskRepository @Inject constructor(
    private val taskDao: TaskDao
) {

    fun getAllTasks(): Flow<List<TaskEntity>> {
        return taskDao.getAllTasks()
    }

    fun getTasksByUserId(userId: Int): Flow<List<TaskEntity>> {
        return taskDao.getTasksByUserId(userId)
    }

    fun getCompletedTasks(): Flow<List<TaskEntity>> {
        return taskDao.getCompletedTasks()
    }

    fun getPendingTasks(): Flow<List<TaskEntity>> {
        return taskDao.getPendingTasks()
    }

    suspend fun insertTask(task: TaskEntity): Long {
        return taskDao.insertTask(task)
    }

    suspend fun updateTask(task: TaskEntity) {
        taskDao.updateTask(task)
    }

    suspend fun deleteTask(task: TaskEntity) {
        taskDao.deleteTask(task)
    }

    suspend fun markTaskAsCompleted(taskId: Int) {
        taskDao.markTaskAsCompleted(taskId)
    }

    suspend fun deleteAllTasks() {
        taskDao.deleteAllTasks()
    }

    suspend fun deleteCompletedTasks() {
        taskDao.deleteCompletedTasks()
    }
}

package ort.argentina.yatay.tp3.tp3_parcial_grupal3.domain.repository
import kotlinx.coroutines.flow.Flow import ort.argentina.yatay.tp3.tp3_parcial_grupal3.domain.model.Task
/** * DOMAIN REPOSITORY INTERFACE - TaskRepository * Define el contrato para operaciones de tareas * La implementación debe estar en la capa de datos */ interface TaskRepository {
/** * Obtener todas las tareas */ fun getAllTasks(): Flow<List<Task>>
/** * Obtener tarea por ID */ suspend fun getTaskById(taskId: Int): Task?
/** * Obtener tareas de un usuario */ fun getTasksByUser(userId: Int): Flow<List<Task>>
/** * Obtener tareas completadas */ fun getCompletedTasks(): Flow<List<Task>>
/** * Obtener tareas pendientes */ fun getPendingTasks(): Flow<List<Task>>
/** * Insertar una tarea */ suspend fun insertTask(task: Task): Long
/** * Insertar múltiples tareas */ suspend fun insertTasks(tasks: List<Task>)
/** * Actualizar una tarea */ suspend fun updateTask(task: Task)
/** * Eliminar una tarea */ suspend fun deleteTask(task: Task)
/** * Eliminar tareas de un usuario */ suspend fun deleteTasksByUser(userId: Int)
/** * Marcar tarea como completada */ suspend fun markTaskAsCompleted(taskId: Int)
/** * Eliminar todas las tareas */ suspend fun deleteAllTasks()
/** * Eliminar tareas completadas */ suspend fun deleteCompletedTasks() }

package ort.argentina.yatay.tp3.tp3_parcial_grupal3.domain.repository

import kotlinx.coroutines.flow.Flow
import ort.argentina.yatay.tp3.tp3_parcial_grupal3.data.local.dao.UserDao
import ort.argentina.yatay.tp3.tp3_parcial_grupal3.data.local.entity.UserEntity
import javax.inject.Inject
import javax.inject.Singleton

/**
 * Repositorio para operaciones de usuarios usando Room
 * Inyecta UserDao automáticamente con Hilt
 */
@Singleton
class UserRepository @Inject constructor(
    private val userDao: UserDao
) {

    /**
     * Obtener todos los usuarios como Flow
     */
    fun getAllUsers(): Flow<List<UserEntity>> {
        return userDao.getAllUsers()
    }

    /**
     * Obtener usuario por ID
     */
    suspend fun getUserById(userId: Int): UserEntity? {
        return userDao.getUserById(userId)
    }

    /**
     * Buscar usuarios por nombre
     */
    fun searchUsers(query: String): Flow<List<UserEntity>> {
        return userDao.searchUsersByName(query)
    }

    /**
     * Insertar nuevo usuario
     */
    suspend fun insertUser(user: UserEntity): Long {
        return userDao.insertUser(user)
    }

    /**
     * Insertar múltiples usuarios
     */
    suspend fun insertUsers(users: List<UserEntity>) {
        userDao.insertUsers(users)
    }

    /**
     * Actualizar usuario existente
     */
    suspend fun updateUser(user: UserEntity) {
        userDao.updateUser(user)
    }

    /**
     * Eliminar usuario
     */
    suspend fun deleteUser(user: UserEntity) {
        userDao.deleteUser(user)
    }

    /**
     * Eliminar todos los usuarios
     */
    suspend fun deleteAllUsers() {
        userDao.deleteAllUsers()
    }

    /**
     * Obtener conteo de usuarios
     */
    suspend fun getUserCount(): Int {
        return userDao.getUserCount()
    }
}

